// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.5.0;

import "./ILayerZeroUserApplicationConfig.sol";

interface ILayerZeroEndpoint is ILayerZeroUserApplicationConfig {
    // @notice send a LayerZero message to the specified address at a LayerZero endpoint.
    // @param _dstChainId - the destination chain identifier
    // @param _destination - the address on destination chain (in bytes). address length/format may vary by chains
    // @param _payload - a custom bytes payload to send to the destination contract
    // @param _refundAddress - if the source transaction is cheaper than the amount of value passed, refund the additional amount to this address
    // @param _zroPaymentAddress - the address of the ZRO token holder who would pay for the transaction
    // @param _adapterParams - parameters for custom functionality. e.g. receive airdropped native gas from the relayer on destination
    function send(
        uint16 _dstChainId,
        bytes calldata _destination,
        bytes calldata _payload,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes calldata _adapterParams
    ) external payable;

    // @notice used by the messaging library to publish verified payload
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source contract (as bytes) at the source chain
    // @param _dstAddress - the address on destination chain
    // @param _nonce - the unbound message ordering nonce
    // @param _gasLimit - the gas limit for external contract execution
    // @param _payload - verified payload to send to the destination contract
    function receivePayload(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        address _dstAddress,
        uint64 _nonce,
        uint _gasLimit,
        bytes calldata _payload
    ) external;

    // @notice get the inboundNonce of a lzApp from a source chain which could be EVM or non-EVM chain
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source chain contract address
    function getInboundNonce(uint16 _srcChainId, bytes calldata _srcAddress)
        external
        view
        returns (uint64);

    // @notice get the outboundNonce from this source chain which, consequently, is always an EVM
    // @param _srcAddress - the source chain contract address
    function getOutboundNonce(uint16 _dstChainId, address _srcAddress)
        external
        view
        returns (uint64);

    // @notice gets a quote in source native gas, for the amount that send() requires to pay for message delivery
    // @param _dstChainId - the destination chain identifier
    // @param _userApplication - the user app address on this EVM chain
    // @param _payload - the custom message to send over LayerZero
    // @param _payInZRO - if false, user app pays the protocol fee in native token
    // @param _adapterParam - parameters for the adapter service, e.g. send some dust native token to dstChain
    function estimateFees(
        uint16 _dstChainId,
        address _userApplication,
        bytes calldata _payload,
        bool _payInZRO,
        bytes calldata _adapterParam
    ) external view returns (uint nativeFee, uint zroFee);

    // @notice get this Endpoint's immutable source identifier
    function getChainId() external view returns (uint16);

    // @notice the interface to retry failed message on this Endpoint destination
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source chain contract address
    // @param _payload - the payload to be retried
    function retryPayload(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        bytes calldata _payload
    ) external;

    // @notice query if any STORED payload (message blocking) at the endpoint.
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source chain contract address
    function hasStoredPayload(uint16 _srcChainId, bytes calldata _srcAddress)
        external
        view
        returns (bool);

    // @notice query if the _libraryAddress is valid for sending msgs.
    // @param _userApplication - the user app address on this EVM chain
    function getSendLibraryAddress(address _userApplication) external view returns (address);

    // @notice query if the _libraryAddress is valid for receiving msgs.
    // @param _userApplication - the user app address on this EVM chain
    function getReceiveLibraryAddress(address _userApplication) external view returns (address);

    // @notice query if the non-reentrancy guard for send() is on
    // @return true if the guard is on. false otherwise
    function isSendingPayload() external view returns (bool);

    // @notice query if the non-reentrancy guard for receive() is on
    // @return true if the guard is on. false otherwise
    function isReceivingPayload() external view returns (bool);

    // @notice get the configuration of the LayerZero messaging library of the specified version
    // @param _version - messaging library version
    // @param _chainId - the chainId for the pending config change
    // @param _userApplication - the contract address of the user application
    // @param _configType - type of configuration. every messaging library has its own convention.
    function getConfig(
        uint16 _version,
        uint16 _chainId,
        address _userApplication,
        uint _configType
    ) external view returns (bytes memory);

    // @notice get the send() LayerZero messaging library version
    // @param _userApplication - the contract address of the user application
    function getSendVersion(address _userApplication) external view returns (uint16);

    // @notice get the lzReceive() LayerZero messaging library version
    // @param _userApplication - the contract address of the user application
    function getReceiveVersion(address _userApplication) external view returns (uint16);
}

contracts/interfaces/ILayerZeroUserAppli.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.5.0;

interface ILayerZeroUserApplicationConfig {
    // @notice set the configuration of the LayerZero messaging library of the specified version
    // @param _version - messaging library version
    // @param _chainId - the chainId for the pending config change
    // @param _configType - type of configuration. every messaging library has its own convention.
    // @param _config - configuration in the bytes. can encode arbitrary content.
    function setConfig(
        uint16 _version,
        uint16 _chainId,
        uint _configType,
        bytes calldata _config
    ) external;

    // @notice set the send() LayerZero messaging library version to _version
    // @param _version - new messaging library version
    function setSendVersion(uint16 _version) external;

    // @notice set the lzReceive() LayerZero messaging library version to _version
    // @param _version - new messaging library version
    function setReceiveVersion(uint16 _version) external;

    // @notice Only when the UA needs to resume the message flow in blocking mode and clear the stored payload
    // @param _srcChainId - the chainId of the source chain
    // @param _srcAddress - the contract address of the source contract at the source chain
    function forceResumeReceive(uint16 _srcChainId, bytes calldata _srcAddress) external;
}

contracts/mocks/LZEndpointMock.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.12;

import "../interfaces/ILayerZeroReceiver.sol";
import "../interfaces/ILayerZeroEndpoint.sol";
import "../libraries/LzLib.sol";

/*
like a real LayerZero enpoint but can be mocked, which handle message transmission, verification, and receipt.
- blocking: LayerZero provides ordered delivery of messages from a given sender to a destination chain.
- non-reentrancy: endpoint has a non-reentrancy guard for both the send() and receive(), respectively.
- adapter parameters: allows UAs to add arbitrary transaction params in the send() function, like airdrop on destination chain.
unlike a real LayerZero endpoint, it is
- no messaging library versioning
- send() will short circuit to lzReceive()
- no user application configuration
*/
contract LZEndpointMock is ILayerZeroEndpoint {
    uint8 internal constant _NOT_ENTERED = 1;
    uint8 internal constant _ENTERED = 2;

    mapping(address => address) public lzEndpointLookup;

    uint16 public mockChainId;
    bool public nextMsgBlocked;
    bool public callReceive; // if true, call lzReceive() in send()

    // fee config
    RelayerFeeConfig public relayerFeeConfig;
    ProtocolFeeConfig public protocolFeeConfig;
    uint public oracleFee;
    bytes public defaultAdapterParams;

    // path = remote addrss + local address
    // inboundNonce = [srcChainId][path].
    mapping(uint16 => mapping(bytes => uint64)) public inboundNonce;
    //todo: this is a hack
    // outboundNonce = [dstChainId][srcAddress]
    mapping(uint16 => mapping(address => uint64)) public outboundNonce;
    //    // outboundNonce = [dstChainId][path].
    //    mapping(uint16 => mapping(bytes => uint64)) public outboundNonce;
    // storedPayload = [srcChainId][path]
    mapping(uint16 => mapping(bytes => StoredPayload)) public storedPayload;
    // msgToDeliver = [srcChainId][path]
    mapping(uint16 => mapping(bytes => QueuedPayload[])) public msgsToDeliver;

    // reentrancy guard
    uint8 internal _send_entered_state = 1;
    uint8 internal _receive_entered_state = 1;

    struct ProtocolFeeConfig {
        uint zroFee;
        uint nativeBP;
    }

    struct RelayerFeeConfig {
        uint128 dstPriceRatio; // 10^10
        uint128 dstGasPriceInWei;
        uint128 dstNativeAmtCap;
        uint64 baseGas;
        uint64 gasPerByte;
    }

    struct StoredPayload {
        uint64 payloadLength;
        address dstAddress;
        bytes32 payloadHash;
    }

    struct QueuedPayload {
        address dstAddress;
        uint64 nonce;
        bytes payload;
    }

    modifier sendNonReentrant() {
        require(_send_entered_state == _NOT_ENTERED, "LayerZeroMock: no send reentrancy");
        _send_entered_state = _ENTERED;
        _;
        _send_entered_state = _NOT_ENTERED;
    }

    modifier receiveNonReentrant() {
        require(_receive_entered_state == _NOT_ENTERED, "LayerZeroMock: no receive reentrancy");
        _receive_entered_state = _ENTERED;
        _;
        _receive_entered_state = _NOT_ENTERED;
    }

    event UaForceResumeReceive(uint16 chainId, bytes srcAddress);
    event PayloadCleared(uint16 srcChainId, bytes srcAddress, uint64 nonce, address dstAddress);
    event PayloadStored(
        uint16 srcChainId,
        bytes srcAddress,
        address dstAddress,
        uint64 nonce,
        bytes payload,
        bytes reason
    );
    event ValueTransferFailed(address indexed to, uint indexed quantity);
    event Packet(bytes payload);

    constructor(uint16 _chainId) {
        mockChainId = _chainId;

        // init config
        relayerFeeConfig = RelayerFeeConfig({
            dstPriceRatio: 1e10, // 1:1, same chain, same native coin
            dstGasPriceInWei: 1e10,
            dstNativeAmtCap: 1e19,
            baseGas: 100,
            gasPerByte: 1
        });
        protocolFeeConfig = ProtocolFeeConfig({zroFee: 1e18, nativeBP: 1000}); // BP 0.1
        oracleFee = 1e16;
        defaultAdapterParams = LzLib.buildDefaultAdapterParams(100000);
    }

    // ------------------------------ ILayerZeroEndpoint Functions ------------------------------
    function send(
        uint16 _chainId,
        bytes memory _path,
        bytes calldata _payload,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes memory _adapterParams
    ) external payable override sendNonReentrant {
        require(_path.length == 40, "LayerZeroMock: incorrect remote address size"); // only support evm chains

        // not handle zro token
        uint16 dstChainId = _chainId;
        bytes memory adapterParams = _adapterParams.length > 0
            ? _adapterParams
            : defaultAdapterParams;
        (uint nativeFee, ) = estimateFees(
            dstChainId,
            msg.sender,
            _payload,
            _zroPaymentAddress != address(0x0),
            adapterParams
        );
        require(msg.value >= nativeFee, "LayerZeroMock: not enough native for fees");

        uint64 nonce = ++outboundNonce[dstChainId][msg.sender];

        // refund if they send too much
        uint amount = msg.value - nativeFee;
        if (amount > 0) {
            (bool success, ) = _refundAddress.call{value: amount}("");
            require(success, "LayerZeroMock: failed to refund");
        }

        // Mock the process of receiving msg on dst chain
        // Mock the relayer paying the dstNativeAddr the amount of extra native token
        (, uint extraGas, uint dstNativeAmt, address payable dstNativeAddr) = LzLib
            .decodeAdapterParams(adapterParams);
        if (dstNativeAmt > 0) {
            (bool success, ) = dstNativeAddr.call{value: dstNativeAmt}("");
            if (!success) {
                emit ValueTransferFailed(dstNativeAddr, dstNativeAmt);
            }
        }

        // emit the data packet
        address dstAddr;
        assembly {
            dstAddr := mload(add(_path, 20))
        }
        bytes memory payload = _payload;
        bytes memory encodedPayload = abi.encodePacked(
            nonce,
            mockChainId,
            msg.sender,
            dstChainId,
            dstAddr,
            payload
        );
        emit Packet(encodedPayload);

        if (callReceive) {
            address lzEndpoint = lzEndpointLookup[dstAddr];
            require(
                lzEndpoint != address(0),
                "LayerZeroMock: destination LayerZero Endpoint not found"
            );

            bytes memory srcUaAddress = abi.encodePacked(msg.sender, dstAddr); // cast this address to bytes
            LZEndpointMock(lzEndpoint).receivePayload(
                mockChainId,
                srcUaAddress,
                dstAddr,
                nonce,
                extraGas,
                payload
            );
        }
    }

    function receivePayload(
        uint16 _srcChainId,
        bytes calldata _path,
        address _dstAddress,
        uint64 _nonce,
        uint _gasLimit,
        bytes calldata _payload
    ) external override receiveNonReentrant {
        StoredPayload storage sp = storedPayload[_srcChainId][_path];

        // assert and increment the nonce. no message shuffling
        require(_nonce == ++inboundNonce[_srcChainId][_path], "LayerZeroMock: wrong nonce");

        // queue the following msgs inside of a stack to simulate a successful send on src, but not fully delivered on dst
        if (sp.payloadHash != bytes32(0)) {
            QueuedPayload[] storage msgs = msgsToDeliver[_srcChainId][_path];
            QueuedPayload memory newMsg = QueuedPayload(_dstAddress, _nonce, _payload);

            // warning, might run into gas issues trying to forward through a bunch of queued msgs
            // shift all the msgs over so we can treat this like a fifo via array.pop()
            if (msgs.length > 0) {
                // extend the array
                msgs.push(newMsg);

                // shift all the indexes up for pop()
                for (uint i = 0; i < msgs.length - 1; i++) {
                    msgs[i + 1] = msgs[i];
                }

                // put the newMsg at the bottom of the stack
                msgs[0] = newMsg;
            } else {
                msgs.push(newMsg);
            }
        } else if (nextMsgBlocked) {
            storedPayload[_srcChainId][_path] = StoredPayload(
                uint64(_payload.length),
                _dstAddress,
                keccak256(_payload)
            );
            emit PayloadStored(_srcChainId, _path, _dstAddress, _nonce, _payload, bytes(""));
            // ensure the next msgs that go through are no longer blocked
            nextMsgBlocked = false;
        } else {
            try
                ILayerZeroReceiver(_dstAddress).lzReceive{gas: _gasLimit}(
                    _srcChainId,
                    _path,
                    _nonce,
                    _payload
                )
            {} catch (bytes memory reason) {
                storedPayload[_srcChainId][_path] = StoredPayload(
                    uint64(_payload.length),
                    _dstAddress,
                    keccak256(_payload)
                );
                emit PayloadStored(_srcChainId, _path, _dstAddress, _nonce, _payload, reason);
                // ensure the next msgs that go through are no longer blocked
                nextMsgBlocked = false;
            }
        }
    }

    function getInboundNonce(uint16 _chainID, bytes calldata _path)
        external
        view
        override
        returns (uint64)
    {
        return inboundNonce[_chainID][_path];
    }

    function getOutboundNonce(uint16 _chainID, address _srcAddress)
        external
        view
        override
        returns (uint64)
    {
        return outboundNonce[_chainID][_srcAddress];
    }

    function estimateFees(
        uint16 _dstChainId,
        address _userApplication,
        bytes memory _payload,
        bool _payInZRO,
        bytes memory _adapterParams
    ) public view returns (uint nativeFee, uint zroFee) {
        bytes memory adapterParams = _adapterParams.length > 0
            ? _adapterParams
            : defaultAdapterParams;

        // Relayer Fee
        uint relayerFee = _getRelayerFee(
            _dstChainId,
            1,
            _userApplication,
            _payload.length,
            adapterParams
        );

        // LayerZero Fee
        uint protocolFee = _getProtocolFees(_payInZRO, relayerFee, oracleFee);
        _payInZRO ? zroFee = protocolFee : nativeFee = protocolFee;

        // return the sum of fees
        nativeFee = nativeFee + relayerFee + oracleFee;
    }

    function getChainId() external view override returns (uint16) {
        return mockChainId;
    }

    function retryPayload(
        uint16 _srcChainId,
        bytes calldata _path,
        bytes calldata _payload
    ) external override {
        StoredPayload storage sp = storedPayload[_srcChainId][_path];
        require(sp.payloadHash != bytes32(0), "LayerZeroMock: no stored payload");
        require(
            _payload.length == sp.payloadLength && keccak256(_payload) == sp.payloadHash,
            "LayerZeroMock: invalid payload"
        );

        address dstAddress = sp.dstAddress;
        // empty the storedPayload
        sp.payloadLength = 0;
        sp.dstAddress = address(0);
        sp.payloadHash = bytes32(0);

        uint64 nonce = inboundNonce[_srcChainId][_path];

        ILayerZeroReceiver(dstAddress).lzReceive(_srcChainId, _path, nonce, _payload);
        emit PayloadCleared(_srcChainId, _path, nonce, dstAddress);
    }

    function hasStoredPayload(uint16 _srcChainId, bytes calldata _path)
        external
        view
        override
        returns (bool)
    {
        StoredPayload storage sp = storedPayload[_srcChainId][_path];
        return sp.payloadHash != bytes32(0);
    }

    function getSendLibraryAddress(address) external view override returns (address) {
        return address(this);
    }

    function getReceiveLibraryAddress(address) external view override returns (address) {
        return address(this);
    }

    function isSendingPayload() external view override returns (bool) {
        return _send_entered_state == _ENTERED;
    }

    function isReceivingPayload() external view override returns (bool) {
        return _receive_entered_state == _ENTERED;
    }

    function getConfig(
        uint16, /*_version*/
        uint16, /*_chainId*/
        address, /*_ua*/
        uint /*_configType*/
    ) external pure override returns (bytes memory) {
        return "";
    }

    function getSendVersion(
        address /*_userApplication*/
    ) external pure override returns (uint16) {
        return 1;
    }

    function getReceiveVersion(
        address /*_userApplication*/
    ) external pure override returns (uint16) {
        return 1;
    }

    function setConfig(
        uint16, /*_version*/
        uint16, /*_chainId*/
        uint, /*_configType*/
        bytes memory /*_config*/
    ) external override {}

    function setSendVersion(
        uint16 /*version*/
    ) external override {}

    function setReceiveVersion(
        uint16 /*version*/
    ) external override {}

    function forceResumeReceive(uint16 _srcChainId, bytes calldata _path) external override {
        StoredPayload storage sp = storedPayload[_srcChainId][_path];
        // revert if no messages are cached. safeguard malicious UA behaviour
        require(sp.payloadHash != bytes32(0), "LayerZeroMock: no stored payload");
        require(sp.dstAddress == msg.sender, "LayerZeroMock: invalid caller");

        // empty the storedPayload
        sp.payloadLength = 0;
        sp.dstAddress = address(0);
        sp.payloadHash = bytes32(0);

        emit UaForceResumeReceive(_srcChainId, _path);

        // resume the receiving of msgs after we force clear the "stuck" msg
        _clearMsgQue(_srcChainId, _path);
    }

    // ------------------------------ Other Public/External Functions --------------------------------------------------

    // used to simulate messages received get stored as a payload
    function blockNextMsg() external {
        nextMsgBlocked = true;
    }

    function setDestLzEndpoint(address destAddr, address lzEndpointAddr) external {
        lzEndpointLookup[destAddr] = lzEndpointAddr;
    }

    function setCallReceive(bool _callReceive) external {
        callReceive = _callReceive;
    }

    function setRelayerPrice(
        uint128 _dstPriceRatio,
        uint128 _dstGasPriceInWei,
        uint128 _dstNativeAmtCap,
        uint64 _baseGas,
        uint64 _gasPerByte
    ) external {
        relayerFeeConfig.dstPriceRatio = _dstPriceRatio;
        relayerFeeConfig.dstGasPriceInWei = _dstGasPriceInWei;
        relayerFeeConfig.dstNativeAmtCap = _dstNativeAmtCap;
        relayerFeeConfig.baseGas = _baseGas;
        relayerFeeConfig.gasPerByte = _gasPerByte;
    }

    function setProtocolFee(uint _zroFee, uint _nativeBP) external {
        protocolFeeConfig.zroFee = _zroFee;
        protocolFeeConfig.nativeBP = _nativeBP;
    }

    function setOracleFee(uint _oracleFee) external {
        oracleFee = _oracleFee;
    }

    function setDefaultAdapterParams(bytes memory _adapterParams) external {
        defaultAdapterParams = _adapterParams;
    }

    // --------------------- Internal Functions ---------------------
    // simulates the relayer pushing through the rest of the msgs that got delayed due to the stored payload
    function _clearMsgQue(uint16 _srcChainId, bytes calldata _path) internal {
        QueuedPayload[] storage msgs = msgsToDeliver[_srcChainId][_path];

        // warning, might run into gas issues trying to forward through a bunch of queued msgs
        while (msgs.length > 0) {
            QueuedPayload memory payload = msgs[msgs.length - 1];
            ILayerZeroReceiver(payload.dstAddress).lzReceive(
                _srcChainId,
                _path,
                payload.nonce,
                payload.payload
            );
            msgs.pop();
        }
    }

    function _getProtocolFees(
        bool _payInZro,
        uint _relayerFee,
        uint _oracleFee
    ) internal view returns (uint) {
        if (_payInZro) {
            return protocolFeeConfig.zroFee;
        } else {
            return ((_relayerFee + _oracleFee) * protocolFeeConfig.nativeBP) / 10000;
        }
    }

    function _getRelayerFee(
        uint16, /* _dstChainId */
        uint16, /* _outboundProofType */
        address, /* _userApplication */
        uint _payloadSize,
        bytes memory _adapterParams
    ) internal view returns (uint) {
        (uint16 txType, uint extraGas, uint dstNativeAmt, ) = LzLib.decodeAdapterParams(
            _adapterParams
        );
        uint totalRemoteToken; // = baseGas + extraGas + requiredNativeAmount
        if (txType == 2) {
            require(
                relayerFeeConfig.dstNativeAmtCap >= dstNativeAmt,
                "LayerZeroMock: dstNativeAmt too large "
            );
            totalRemoteToken += dstNativeAmt;
        }
        // remoteGasTotal = dstGasPriceInWei * (baseGas + extraGas)
        uint remoteGasTotal = relayerFeeConfig.dstGasPriceInWei *
            (relayerFeeConfig.baseGas + extraGas);
        totalRemoteToken += remoteGasTotal;

        // tokenConversionRate = dstPrice / localPrice
        // basePrice = totalRemoteToken * tokenConversionRate
        uint basePrice = (totalRemoteToken * relayerFeeConfig.dstPriceRatio) / 10**10;

        // pricePerByte = (dstGasPriceInWei * gasPerBytes) * tokenConversionRate
        uint pricePerByte = (relayerFeeConfig.dstGasPriceInWei *
            relayerFeeConfig.gasPerByte *
            relayerFeeConfig.dstPriceRatio) / 10**10;

        return basePrice + _payloadSize * pricePerByte;
    }
}

contracts/interfaces/ILayerZeroReceiver..content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.5.0;

interface ILayerZeroReceiver {
    // @notice LayerZero endpoint will invoke this function to deliver the message on the destination
    // @param _srcChainId - the source endpoint identifier
    // @param _srcAddress - the source sending contract address from the source chain
    // @param _nonce - the ordered message nonce
    // @param _payload - the signed payload is the UA bytes has encoded to be sent
    function lzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64 _nonce,
        bytes calldata _payload
    ) external;
}

contracts/libraries/LzLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.6.0;
pragma experimental ABIEncoderV2;

library LzLib {
    // LayerZero communication
    struct CallParams {
        address payable refundAddress;
        address zroPaymentAddress;
    }

    //---------------------------------------------------------------------------
    // Address type handling

    struct AirdropParams {
        uint airdropAmount;
        bytes32 airdropAddress;
    }

    function buildAdapterParams(LzLib.AirdropParams memory _airdropParams, uint _uaGasLimit)
        internal
        pure
        returns (bytes memory adapterParams)
    {
        if (_airdropParams.airdropAmount == 0 && _airdropParams.airdropAddress == bytes32(0x0)) {
            adapterParams = buildDefaultAdapterParams(_uaGasLimit);
        } else {
            adapterParams = buildAirdropAdapterParams(_uaGasLimit, _airdropParams);
        }
    }

    // Build Adapter Params
    function buildDefaultAdapterParams(uint _uaGas) internal pure returns (bytes memory) {
        // txType 1
        // bytes  [2       32      ]
        // fields [txType  extraGas]
        return abi.encodePacked(uint16(1), _uaGas);
    }

    function buildAirdropAdapterParams(uint _uaGas, AirdropParams memory _params)
        internal
        pure
        returns (bytes memory)
    {
        require(_params.airdropAmount > 0, "Airdrop amount must be greater than 0");
        require(_params.airdropAddress != bytes32(0x0), "Airdrop address must be set");

        // txType 2
        // bytes  [2       32        32            bytes[]         ]
        // fields [txType  extraGas  dstNativeAmt  dstNativeAddress]
        return abi.encodePacked(uint16(2), _uaGas, _params.airdropAmount, _params.airdropAddress);
    }

    function getGasLimit(bytes memory _adapterParams) internal pure returns (uint gasLimit) {
        require(_adapterParams.length == 34 || _adapterParams.length > 66, "Invalid adapterParams");
        assembly {
            gasLimit := mload(add(_adapterParams, 34))
        }
    }

    // Decode Adapter Params
    function decodeAdapterParams(bytes memory _adapterParams)
        internal
        pure
        returns (
            uint16 txType,
            uint uaGas,
            uint airdropAmount,
            address payable airdropAddress
        )
    {
        require(_adapterParams.length == 34 || _adapterParams.length > 66, "Invalid adapterParams");
        assembly {
            txType := mload(add(_adapterParams, 2))
            uaGas := mload(add(_adapterParams, 34))
        }
        require(txType == 1 || txType == 2, "Unsupported txType");
        require(uaGas > 0, "Gas too low");

        if (txType == 2) {
            assembly {
                airdropAmount := mload(add(_adapterParams, 66))
                airdropAddress := mload(add(_adapterParams, 86))
            }
        }
    }

    //---------------------------------------------------------------------------
    // Address type handling
    function bytes32ToAddress(bytes32 _bytes32Address) internal pure returns (address _address) {
        return address(uint160(uint(_bytes32Address)));
    }

    function addressToBytes32(address _address) internal pure returns (bytes32 _bytes32Address) {
        return bytes32(uint(uint160(_address)));
    }
}

contracts/TokenBridge.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.15;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./interfaces/IWETH.sol";
import "./interfaces/ITokenBridge.sol";
import "./lzApp/NonblockingLzApp.sol";
import "./libraries/LzLib.sol";

contract TokenBridge is ITokenBridge, NonblockingLzApp, Pausable, ReentrancyGuard {
    bytes4 private constant TRANSFER_FROM_SELECTOR =
        bytes4(keccak256(bytes("transferFrom(address,address,uint256)")));
    bytes4 private constant BALANCE_OF_SELECTOR = bytes4(keccak256(bytes("balanceOf(address)")));
    uint public constant BP_DENOMINATOR = 10000;
    uint8 public constant SHARED_DECIMALS = 6;

    uint16 public immutable localChainId;
    uint16 public immutable aptosChainId;

    uint public bridgeFeeBP;
    mapping(address => uint) public accruedFeeLDs; // token address => fee accrued
    mapping(address => uint) public tvlSDs; // token address => tvl
    mapping(address => bool) public supportedTokens;
    mapping(address => uint) public ld2sdRates; // token address => rate
    address public weth;

    constructor(address _layerZeroEndpoint, uint16 _localChainId, uint16 _aptosChainId)
        NonblockingLzApp(_layerZeroEndpoint)
    {
        localChainId = _localChainId;
        aptosChainId = _aptosChainId;
    }

    function sendToAptos(
        address _token,
        bytes32 _toAddress,
        uint _amountLD,
        LzLib.CallParams calldata _callParams,
        bytes calldata _adapterParams
    ) public payable override whenNotPaused nonReentrant {
        require(supportedTokens[_token], "TokenBridge: token is not supported");

        // lock token
        _amountLD = _lockTokenFrom(_token, msg.sender, _amountLD);
        _amountLD = _removeDust(_token, _amountLD);
        require(_amountLD > 0, "TokenBridge: amountLD must be greater than 0");

        // add tvl
        uint64 amountSD = _LDtoSD(_token, _amountLD);
        tvlSDs[_token] += amountSD;

        // send to aptos
        _sendToken(_token, _toAddress, amountSD, _callParams, _adapterParams, msg.value);
        emit Send(_token, msg.sender, _toAddress, _amountLD);
    }

    function sendETHToAptos(
        bytes32 _toAddress,
        uint _amountLD,
        LzLib.CallParams calldata _callParams,
        bytes calldata _adapterParams
    ) public payable override whenNotPaused nonReentrant {
        address _weth = weth; // save gas
        require(_weth != address(0) && supportedTokens[_weth], "TokenBridge: ETH is not supported");
        _amountLD = _removeDust(_weth, _amountLD);
        require(_amountLD > 0, "TokenBridge: amount must be greater than 0");
        require(msg.value > _amountLD, "TokenBridge: fee not enough");

        // wrap eth and add tvl
        IWETH(_weth).deposit{value: _amountLD}();
        uint64 amountSD = _LDtoSD(_weth, _amountLD);
        tvlSDs[_weth] += amountSD;

        // send to aptos
        _sendToken(_weth, _toAddress, amountSD, _callParams, _adapterParams, msg.value - _amountLD);
        emit Send(address(0), msg.sender, _toAddress, _amountLD);
    }

    function quoteForSend(LzLib.CallParams calldata _callParams, bytes calldata _adapterParams)
        public
        view
        returns (uint nativeFee, uint zroFee)
    {
        bytes memory payload = _encodeSendPayload(address(0), bytes32(0), 0);
        bool payInZRO = _callParams.zroPaymentAddress != address(0);
        return
            lzEndpoint.estimateFees(aptosChainId, address(this), payload, payInZRO, _adapterParams);
    }

    // ---------------------- owner functions ----------------------
    function registerToken(address _token) public onlyOwner {
        require(_token != address(0), "TokenBridge: invalid token address");
        require(!supportedTokens[_token], "TokenBridge: token already registered");

        uint8 decimals = _tokenDecimals(_token);
        require(decimals >= SHARED_DECIMALS, "TokenBridge: decimals must be >= SHARED_DECIMALS");

        supportedTokens[_token] = true;
        ld2sdRates[_token] = 10**(decimals - SHARED_DECIMALS);
    }

    function setBridgeFeeBP(uint _bridgeFeeBP) public onlyOwner {
        require(_bridgeFeeBP <= BP_DENOMINATOR, "TokenBridge: bridge fee > 100%");
        bridgeFeeBP = _bridgeFeeBP;
    }

    function withdrawFee(
        address _token,
        address _to,
        uint _amountLD
    ) public onlyOwner {
        accruedFeeLDs[_token] -= _amountLD;
        _safeTransferFrom(_token, address(this), _to, _amountLD);
    }

    function setWETH(address _weth) public onlyOwner {
        require(_weth != address(0), "TokenBridge: invalid WETH address");
        require(weth == address(0), "TokenBridge: WETH address already set"); // immutable
        weth = _weth;
    }

    function setPause(bool _paused) public onlyOwner {
        if (_paused) {
            _pause();
        } else {
            _unpause();
        }
    }

    // receive ETH from WETH
    receive() external payable {}

    // ---------------------- internal functions ----------------------
    function _nonblockingLzReceive(
        uint16 _srcChainId,
        bytes calldata,
        uint64,
        bytes calldata _payload
    ) internal override whenNotPaused {
        require(_srcChainId == aptosChainId, "TokenBridge: invalid source chain id");

        (address token, address to, uint64 amountSD, bool unwrap) = _decodeReceivePayload(_payload);
        require(supportedTokens[token], "TokenBridge: token is not supported");

        // sub tvl
        uint tvlSD = tvlSDs[token];
        require(tvlSD >= amountSD, "TokenBridge: insufficient liquidity");
        tvlSDs[token] = tvlSD - amountSD;

        // pay fee
        uint amountLD = _SDtoLD(token, amountSD);
        (amountLD, ) = _payFee(token, amountLD);

        // redeem token to receiver
        address _weth = weth; // save gas
        if (token == _weth && unwrap) {
            _redeemETHTo(_weth, payable(to), amountLD);
            emit Receive(address(0), to, amountLD);
        } else {
            to = to == address(0) ? address(0xdEaD) : to; // avoid failure in _safeTransferFrom
            _safeTransferFrom(token, address(this), to, amountLD);
            emit Receive(token, to, amountLD);
        }
    }

    function _redeemETHTo(
        address _weth,
        address payable _to,
        uint _amountLD
    ) public {
        IWETH(_weth).withdraw(_amountLD);
        _to.transfer(_amountLD);
    }

    function _lockTokenFrom(
        address _token,
        address _from,
        uint _amountLD
    ) internal returns (uint) {
        // support token with transfer fee
        uint balanceBefore = _tokenBalanceOf(_token, address(this));
        _safeTransferFrom(_token, _from, address(this), _amountLD);
        uint balanceAfter = _tokenBalanceOf(_token, address(this));
        return balanceAfter - balanceBefore;
    }

    function _tokenDecimals(address _token) internal view returns (uint8) {
        (bool success, bytes memory data) = _token.staticcall(
            abi.encodeWithSignature("decimals()")
        );
        require(success, "TokenBridge: failed to get token decimals");
        return abi.decode(data, (uint8));
    }

    function _tokenBalanceOf(address _token, address _account) internal view returns (uint) {
        (bool success, bytes memory data) = _token.staticcall(
            abi.encodeWithSelector(BALANCE_OF_SELECTOR, _account)
        );
        require(success, "TokenBridge: failed to get token balance");
        return abi.decode(data, (uint));
    }

    function _safeTransferFrom(
        address _token,
        address _from,
        address _to,
        uint _amountLD
    ) internal {
        (bool success, bytes memory data) = _token.call(
            abi.encodeWithSelector(TRANSFER_FROM_SELECTOR, _from, _to, _amountLD)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TokenBridge: failed to transfer token"
        );
    }

    function _payFee(address _token, uint _amountLD)
        internal
        returns (uint amountAfterFee, uint fee)
    {
        fee = (_amountLD * bridgeFeeBP) / BP_DENOMINATOR;
        if (fee > 0) {
            accruedFeeLDs[_token] += fee;
        }
        return (_amountLD - fee, fee);
    }

    function _sendToken(
        address _token,
        bytes32 _toAddress,
        uint64 _amountSD,
        LzLib.CallParams calldata _callParams,
        bytes calldata _adapterParams,
        uint _fee
    ) internal {
        bytes memory payload = _encodeSendPayload(_token, _toAddress, _amountSD);
        _lzSend(
            aptosChainId,
            payload,
            _callParams.refundAddress,
            _callParams.zroPaymentAddress,
            _adapterParams,
            _fee
        );
    }

    // payload: packet type(1) + remote token(32) + receiver(32) + amount(8)
    function _encodeSendPayload(
        address _token,
        bytes32 _toAddress,
        uint64 _amountSD
    ) internal pure returns (bytes memory) {
        bytes32 tokenBytes32 = LzLib.addressToBytes32(_token);
        return
            abi.encodePacked(uint8(PacketType.SEND_TO_APTOS), tokenBytes32, _toAddress, _amountSD);
    }

    // payload: packet type(1) + remote token(32) + receiver(32) + amount(8) + unwrap flag(1)
    function _decodeReceivePayload(bytes calldata _payload)
        internal
        pure
        returns (
            address token,
            address to,
            uint64 amountSD,
            bool unwrap
        )
    {
        require(_payload.length == 74, "TokenBridge: invalid payload length");
        PacketType packetType = PacketType(uint8(_payload[0]));
        require(packetType == PacketType.RECEIVE_FROM_APTOS, "TokenBridge: unknown packet type");

        token = LzLib.bytes32ToAddress(bytes32(_payload[1:33]));
        to = LzLib.bytes32ToAddress(bytes32(_payload[33:65]));
        amountSD = uint64(bytes8(_payload[65:73]));
        unwrap = uint8(_payload[73]) == 1;
    }

    function _SDtoLD(address _token, uint64 _amountSD) internal view returns (uint) {
        return _amountSD * ld2sdRates[_token];
    }

    function _LDtoSD(address _token, uint _amountLD) internal view returns (uint64) {
        uint amountSD = _amountLD / ld2sdRates[_token];
        require(amountSD <= type(uint64).max, "TokenBridge: amountSD overflow");
        return uint64(amountSD);
    }

    function _removeDust(address _token, uint _amountLD) internal view returns (uint) {
        return _SDtoLD(_token, _LDtoSD(_token, _amountLD));
    }
}

